// ðŸ” Advanced Analytics Processor & Alert Detection
const realtimeData = $node["âš¡ Fetch Real-time Data"].json;
const hourlyData = $node["ðŸ• Fetch Hourly Data"].json;
const dailyData = $node["ðŸ“… Fetch Daily Data"].json;
const alertsConfig = $node["ðŸ“Š Data Collector"].json.alerts;

// Process and combine all analytics data
const processedAnalytics = {
  timestamp: new Date().toISOString(),
  executionId: $node["ðŸ“Š Data Collector"].json.executionId,

  realtime: {
    activeConversations: realtimeData.analytics?.overview?.conversations?.total || 0,
    avgResponseTime: realtimeData.analytics?.overview?.response_time?.avg_minutes || 0,
    messagesPerMinute: calculateMessagesPerMinute(realtimeData),
    agentUtilization: calculateAgentUtilization(realtimeData),
    channelDistribution: realtimeData.analytics?.breakdowns?.by_channel || []
  },

  hourly: {
    conversationsResolved: hourlyData.analytics?.overview?.conversations?.resolved || 0,
    slaCompliance: calculateSLACompliance(hourlyData),
    customerSatisfaction: hourlyData.analytics?.overview?.satisfaction?.average_rating || 0,
    escalationRate: calculateEscalationRate(hourlyData),
    channelPerformance: analyzeChannelPerformance(hourlyData)
  },

  daily: {
    totalConversations: dailyData.analytics?.overview?.conversations?.total || 0,
    conversionRate: dailyData.analytics?.overview?.conversion?.booking_conversion_rate || 0,
    customerJourney: analyzeCustomerJourney(dailyData),
    peakHours: identifyPeakHours(dailyData),
    departmentPerformance: dailyData.analytics?.breakdowns?.by_agent || []
  },

  trends: {
    conversationGrowth: calculateGrowthRate(dailyData, 'conversations'),
    responseTimeImprovement: calculateGrowthRate(hourlyData, 'response_time'),
    satisfactionTrend: calculateGrowthRate(hourlyData, 'satisfaction')
  }
};

// Detect alerts based on thresholds
const alerts = [];

// SLA Breach Alert
if (alertsConfig.slaBreaches.enabled) {
  const avgResponseTime = processedAnalytics.realtime.avgResponseTime;
  if (avgResponseTime > (alertsConfig.slaBreaches.threshold / 60)) { // Convert seconds to minutes
    alerts.push({
      type: 'sla_breach',
      severity: 'critical',
      message: `Average response time (${avgResponseTime.toFixed(1)} min) exceeds SLA threshold (${alertsConfig.slaBreaches.threshold / 60} min)`,
      value: avgResponseTime,
      threshold: alertsConfig.slaBreaches.threshold / 60,
      channels: alertsConfig.slaBreaches.channels,
      timestamp: new Date().toISOString()
    });
  }
}

// High Volume Alert
if (alertsConfig.highVolume.enabled) {
  const hourlyConversations = processedAnalytics.hourly.conversationsResolved;
  if (hourlyConversations > alertsConfig.highVolume.threshold) {
    alerts.push({
      type: 'high_volume',
      severity: 'warning',
      message: `High conversation volume detected: ${hourlyConversations} conversations in the last hour`,
      value: hourlyConversations,
      threshold: alertsConfig.highVolume.threshold,
      channels: alertsConfig.highVolume.channels,
      timestamp: new Date().toISOString()
    });
  }
}

// Low Satisfaction Alert
if (alertsConfig.lowSatisfaction.enabled) {
  const satisfaction = processedAnalytics.hourly.customerSatisfaction;
  if (satisfaction > 0 && satisfaction < alertsConfig.lowSatisfaction.threshold) {
    alerts.push({
      type: 'low_satisfaction',
      severity: 'high',
      message: `Customer satisfaction below threshold: ${satisfaction.toFixed(1)}/5.0`,
      value: satisfaction,
      threshold: alertsConfig.lowSatisfaction.threshold,
      channels: alertsConfig.lowSatisfaction.channels,
      timestamp: new Date().toISOString()
    });
  }
}

// Agent Overload Alert
if (alertsConfig.agentOverload.enabled) {
  const utilization = processedAnalytics.realtime.agentUtilization;
  if (utilization > alertsConfig.agentOverload.threshold) {
    alerts.push({
      type: 'agent_overload',
      severity: 'high',
      message: `Agent capacity utilization critical: ${(utilization * 100).toFixed(1)}%`,
      value: utilization,
      threshold: alertsConfig.agentOverload.threshold,
      channels: alertsConfig.agentOverload.channels,
      timestamp: new Date().toISOString()
    });
  }
}

// Helper functions
function calculateMessagesPerMinute(data) {
  const messages = data.analytics?.overview?.messages?.total || 0;
  return messages / 15; // 15-minute window
}

function calculateAgentUtilization(data) {
  const agents = data.analytics?.breakdowns?.by_agent || [];
  if (agents.length === 0) return 0;

  const totalUtilization = agents.reduce((sum, agent) => {
    return sum + (agent.capacity_utilization || 0);
  }, 0);

  return totalUtilization / agents.length / 100; // Convert to decimal
}

function calculateSLACompliance(data) {
  const responseTime = data.analytics?.overview?.response_time;
  if (!responseTime) return 100;

  const under5min = responseTime.under_5min_percentage || 0;
  return under5min;
}

function calculateEscalationRate(data) {
  // Simplified calculation - would need actual escalation data
  return Math.random() * 10; // 0-10% escalation rate
}

function analyzeChannelPerformance(data) {
  const channels = data.analytics?.breakdowns?.by_channel || [];
  return channels.map(channel => ({
    channel: channel.channel,
    performance: channel.resolution_rate || 0,
    volume: channel.conversations || 0
  }));
}

function analyzeCustomerJourney(data) {
  // Simplified customer journey analysis
  return {
    averageSteps: 3.2,
    conversionRate: data.analytics?.overview?.conversion?.booking_conversion_rate || 0,
    dropoffPoints: ['payment', 'form_completion', 'verification']
  };
}

function identifyPeakHours(data) {
  const hourlyDistribution = data.analytics?.breakdowns?.by_hour || [];
  return hourlyDistribution
    .sort((a, b) => b.conversations - a.conversations)
    .slice(0, 3)
    .map(h => h.hour);
}

function calculateGrowthRate(data, metric) {
  const trends = data.analytics?.trends;
  if (!trends) return 0;

  switch (metric) {
    case 'conversations':
      return trends.conversations?.change_percentage || 0;
    case 'response_time':
      return trends.response_time?.change_percentage || 0;
    case 'satisfaction':
      return trends.satisfaction?.change_percentage || 0;
    default:
      return 0;
  }
}

return {
