function calculateCompleteness(analytics) {
  let totalFields = 0;
  let filledFields = 0;

  function countFields(obj) {
    for (const key in obj) {
      if (typeof obj[key] === 'object' && obj[key] !== null) {
        countFields(obj[key]);
      } else {
        totalFields++;
        if (obj[key] !== null && obj[key] !== undefined && obj[key] !== 0) {
          filledFields++;
        }
      }
    }
  }

  countFields(analytics);
  return totalFields > 0 ? (filledFields / totalFields) * 100 : 0;
}

function calculateFreshness(analytics) {
  const now = Date.now();
  const dataTimestamp = new Date(analytics.timestamp || new Date()).getTime();
  const ageMinutes = (now - dataTimestamp) / (1000 * 60);

  // Data is considered fresh if less than 20 minutes old
  return Math.max(0, 100 - (ageMinutes / 20) * 100);
}

function calculateAccuracy(analytics) {
  // Simple accuracy check based on data consistency
  let consistencyScore = 100;

  // Check if active conversations is reasonable
  if (analytics.realtime.activeConversations < 0 || analytics.realtime.activeConversations > 1000) {
    consistencyScore -= 20;
  }

  // Check if response time is reasonable
  if (analytics.realtime.avgResponseTime < 0 || analytics.realtime.avgResponseTime > 120) {
    consistencyScore -= 20;
  }

  // Check if satisfaction score is in valid range
  if (analytics.hourly.customerSatisfaction < 0 || analytics.hourly.customerSatisfaction > 5) {
    consistencyScore -= 20;
  }

  return Math.max(0, consistencyScore);
}

