// ğŸ¯ Multi-Platform Message Router
const payload = $input.all()[0].json;
const headers = $input.all()[0].headers;

// Detect platform from webhook data
let platform = 'unknown';
let messageData = {};

// Instagram Detection
if (payload.object === 'instagram' && payload.entry) {
  platform = 'instagram';
  messageData = processInstagramWebhook(payload);
}
// Facebook Detection
else if (payload.object === 'page' && payload.entry) {
  platform = 'facebook';
  messageData = processFacebookWebhook(payload);
}
// Telegram Detection
else if (payload.message || payload.callback_query) {
  platform = 'telegram';
  messageData = processTelegramWebhook(payload);
}
// WhatsApp Detection (Meta Business API)
else if (payload.object === 'whatsapp_business_account') {
  platform = 'whatsapp';
  messageData = processWhatsAppWebhook(payload);
}
// LinkedIn Detection
else if (headers['linkedin-signature']) {
  platform = 'linkedin';
  messageData = processLinkedInWebhook(payload);
}
// Twitter/X Detection
else if (headers['x-twitter-webhooks-signature']) {
  platform = 'twitter';
  messageData = processTwitterWebhook(payload);
}

function processInstagramWebhook(payload) {
  const entry = payload.entry[0];
  const messaging = entry.messaging?.[0];

  if (!messaging?.message) return null;

  return {
    platform: 'instagram',
    senderId: messaging.sender.id,
    messageId: messaging.message.mid,
    content: messaging.message.text || '[MÃ­dia recebida]',
    timestamp: new Date(messaging.timestamp).toISOString(),
    messageType: messaging.message.attachments ? 'media' : 'text',
    attachments: messaging.message.attachments || [],
    raw: payload
  };
}

function processFacebookWebhook(payload) {
  const entry = payload.entry[0];
  const messaging = entry.messaging?.[0];

  if (!messaging?.message || messaging.message.is_echo) return null;

  return {
    platform: 'facebook',
    senderId: messaging.sender.id,
    messageId: messaging.message.mid,
    content: messaging.message.text || '[MÃ­dia recebida]',
    timestamp: new Date(parseInt(messaging.timestamp)).toISOString(),
    messageType: messaging.message.attachments ? 'media' : 'text',
    attachments: messaging.message.attachments || [],
    quickReply: messaging.message.quick_reply,
    raw: payload
  };
}

function processTelegramWebhook(payload) {
  const message = payload.message || payload.edited_message;
  const callbackQuery = payload.callback_query;

  if (callbackQuery) {
    return {
      platform: 'telegram',
      senderId: callbackQuery.from.id.toString(),
      messageId: `callback_${callbackQuery.id}`,
      content: `Callback: ${callbackQuery.data}`,
      timestamp: new Date().toISOString(),
      messageType: 'callback',
      callbackData: callbackQuery.data,
      raw: payload
    };
  }

  if (!message || message.chat.type !== 'private') return null;

  return {
    platform: 'telegram',
    senderId: message.from.id.toString(),
    messageId: `tg_${message.message_id}`,
    content: message.text || getAttachmentDescription(message),
    timestamp: new Date(message.date * 1000).toISOString(),
    messageType: getMessageType(message),
    attachments: getAttachments(message),
    raw: payload
  };
}

function processWhatsAppWebhook(payload) {
  const entry = payload.entry[0];
  const changes = entry.changes[0];
  const messages = changes.value.messages;

  if (!messages || messages.length === 0) return null;

  const message = messages[0];

  return {
    platform: 'whatsapp',
    senderId: message.from,
    messageId: message.id,
    content: message.text?.body || '[MÃ­dia recebida]',
    timestamp: new Date(parseInt(message.timestamp) * 1000).toISOString(),
    messageType: message.type,
    attachments: getWhatsAppAttachments(message),
    raw: payload
  };
}

function processLinkedInWebhook(payload) {
  // Simplified LinkedIn processing
  return {
    platform: 'linkedin',
    senderId: payload.senderId || 'unknown',
    messageId: payload.messageId || `li_${Date.now()}`,
    content: payload.content || 'LinkedIn message',
    timestamp: new Date().toISOString(),
    messageType: 'text',
    raw: payload
  };
}

function processTwitterWebhook(payload) {
  // Simplified Twitter/X processing
  return {
    platform: 'twitter',
    senderId: payload.senderId || 'unknown',
    messageId: payload.messageId || `tw_${Date.now()}`,
    content: payload.content || 'Twitter message',
    timestamp: new Date().toISOString(),
    messageType: 'text',
    raw: payload
  };
}

function getAttachmentDescription(message) {
  if (message.photo) return '[Foto enviada]';
  if (message.document) return `[Documento: ${message.document.file_name || 'arquivo'}]`;
  if (message.voice) return '[Mensagem de voz]';
  if (message.video) return '[VÃ­deo enviado]';
  if (message.sticker) return '[Sticker enviado]';
  return '[Mensagem sem texto]';
}

function getMessageType(message) {
  if (message.text) return 'text';
  if (message.photo) return 'photo';
  if (message.document) return 'document';
  if (message.voice) return 'voice';
  if (message.video) return 'video';
  return 'unknown';
}

function getAttachments(message) {
  const attachments = [];
  if (message.photo) attachments.push({ type: 'photo', data: message.photo });
  if (message.document) attachments.push({ type: 'document', data: message.document });
  if (message.voice) attachments.push({ type: 'voice', data: message.voice });
  if (message.video) attachments.push({ type: 'video', data: message.video });
  return attachments;
}

function getWhatsAppAttachments(message) {
  const attachments = [];
  if (message.image) attachments.push({ type: 'image', data: message.image });
  if (message.document) attachments.push({ type: 'document', data: message.document });
  if (message.audio) attachments.push({ type: 'audio', data: message.audio });
  if (message.video) attachments.push({ type: 'video', data: message.video });
  return attachments;
}

return {
  platform,
  messageData,
  isValid: messageData !== null,
  timestamp: new Date().toISOString(),
  webhook: {
    headers: headers,
    body: payload
  }
};

