// Enhanced WhatsApp message processing with AI classification
const body = $json.body;
let messageData = {};

// Handle different webhook formats
if (body.event === 'whatsapp_message_received') {
  messageData = {
    from: body.data.from,
    message: body.data.text || body.data.message,
    contactName: body.data.contactName || '',
    timestamp: body.data.timestamp,
    messageId: body.data.messageId,
    isNewConversation: body.data.isNewConversation,
    phone: body.data.phone || body.data.from.split('@')[0]
  };
} else {
  messageData = {
    from: body.from || body.phone,
    message: body.message || body.text,
    contactName: body.contactName || body.name || '',
    timestamp: body.timestamp || new Date().toISOString(),
    messageId: body.messageId || `msg_${Date.now()}`,
    isNewConversation: body.isNewConversation || false,
    phone: body.phone || body.from?.split('@')[0]
  };
}

// Advanced intent detection with ML-like scoring
const message = messageData.message.toLowerCase();
let intent = 'general';
let priority = 'normal';
let confidence = 0;
let autoResponse = false;
let requiresHuman = false;

// Intent patterns with confidence scoring
const intentPatterns = {
  greeting: {
