    requiresHuman: true
  },
  cancellation: {
    patterns: ['cancelar', 'cancel', 'refund', 'reembolso', 'devolver'],
    confidence: 0.9,
    priority: 'high',
    requiresHuman: true
  }
};

// Calculate intent with highest confidence
let maxConfidence = 0;
for (const [intentName, config] of Object.entries(intentPatterns)) {
  const patternScore = config.patterns.reduce((score, pattern) => {
    if (message.includes(pattern)) {
      return Math.max(score, config.confidence);
    }
    return score;
  }, 0);

  if (patternScore > maxConfidence) {
    maxConfidence = patternScore;
    intent = intentName;
    confidence = patternScore;
    if (config.autoResponse) autoResponse = true;
    if (config.priority) priority = config.priority;
    if (config.requiresHuman) requiresHuman = true;
  }
}

// Business hours calculation (EST)
const now = new Date();
const estTime = new Date(now.toLocaleString("en-US", {timeZone: "America/New_York"}));
const hour = estTime.getHours();
const day = estTime.getDay();
const isBusinessHours = (day >= 1 && day <= 5 && hour >= 9 && hour < 18) || (day === 6 && hour >= 9 && hour < 14);

// Customer profile scoring
let customerScore = 0;
if (messageData.isNewConversation) customerScore += 20;
if (intent === 'flight_inquiry') customerScore += 30;
if (intent === 'pricing') customerScore += 25;
if (priority === 'high') customerScore += 40;

// Advanced routing logic
let routingDecision = 'auto_response';
if (requiresHuman || priority === 'high') {
  routingDecision = 'human_agent';
} else if (!isBusinessHours && intent !== 'greeting') {
  routingDecision = 'after_hours_response';
} else if (confidence < 0.7) {
  routingDecision = 'clarification_needed';
}

return {
  ...messageData,
  intent,
  confidence: Math.round(confidence * 100),
  priority,
  isBusinessHours,
  customerScore,
  routingDecision,
  autoResponse,
  requiresHuman,
  metadata: {
    processingTime: new Date().toISOString(),
    intentConfidence: confidence,
    messageLength: messageData.message.length,
    hasEmojis: /[\u{1F600}-\u{1F6FF}]/u.test(messageData.message),
    language: message.includes('hello') || message.includes('hi') ? 'en' : 'pt'
  }
};

// Generate intelligent auto-responses with personalization
